INSERT INTO `movie` VALUES ('40029', '女集中营', '洪玉兰（李海淑）与三位美籍女护士：珍妮花（碧蒂．杜芙）、伊莉萨白（妮姬．蕙）和玛丽（鲁思．嘉璐莎）及数十位女子，在日本侵华期间被日军关进女集中 营，她们都惨遭日军的蹂躏摧残，服苦役，受毒打，遭奸污，受尽折磨。女囚们千方百计想逃出日军的控制，后来得到日本翻译官崔国栋（罗烈）的帮助逃出集中 营，并导致了一场激烈的搏斗。', '5.5', '1973-12-21');
