INSERT INTO `movie` VALUES ('83960', '南洋十大邪术', '阿邦、阿江、阿南和Kenny是共患难的好兄弟，一次联袂往泰国旅行，在偶然的机会下，阿邦无意帮助了降头师乃蜜把另一降头师击退，乃蜜并邀请四人回家过宿，而其妹水抹对阿邦情大独钟，要乃蜜对邦下爱情降，谁料竟下错于其馀三人身上，弄得水抹羞愤而死，恶梦便从此开始……', '5.2', '1995-08-04');
