INSERT INTO `movie` VALUES ('91759', 'Laughing Gor之潜罪犯', '影片承接《潜行狙击》电视剧结局，Laughing谋杀苏星柏罪名成立，判处无期徒刑。Laughing在狱中认识贩毒被囚的大学教授。教授外表斯文，与世无争，但却能以心理陷阱收服穷凶极恶的狱霸「巨川」。原来教授是高智商的犯罪心理学家，擅用冷读法，每每能洞悉对方内心秘密与阴暗面，无声无色掌控他人...........', '5.8', '2011-12-29');
