INSERT INTO `movie` VALUES ('93532', '魔胎', '十一年前，香港的富家郑家老太太和她的二媳妇在七月盂兰节的卖物会上高价投得了一块晶莹古玉。岂料自此二嫂性情大变，一日，郑家二爷提前回家，撞见了二嫂与古玉变得怪物在偷情，一怒之下，二爷摔碎了古玉。不料此后二爷浑身腐烂痛极跳楼而死，二嫂不久亦坠楼身亡。', '5.6', '1983-09-07');
