INSERT INTO `movie` VALUES ('130532', '少林殭尸天极', '明朝年间，朝廷屡贡不休，导致马贼猖獗为患，遭受马贼洗劫涂炭的民众无不提心吊胆惶惑不安。在没有朝廷出兵清剿的援助下，民间惟有以自己有限的能力与马贼对抗。但在灭绝人性的马贼面前，民间的抵抗显得无济于事，常常是马贼肆虐过的地方，尸横遍野血流成河。为救民众免遭马贼的涂炭，佛界、道界和江湖武林纷纷出手。这其间，天龙派高手赵龙夫妇在与马贼首领交手中，双双中毒，危在旦夕。', '0', '2006-04-28');
