INSERT INTO `movie` VALUES ('155114', '地下情', '香港少女阮贝儿（温碧霞）和来自台湾的廖玉屏（金燕玲）、赵淑珍（蔡琴）皆以成名为人生目的，但现实证明她们离当明星的生活仍有一段不短的距离。三人在酒廊偶识米铺少爷张树东（梁朝伟），不久阮贝儿与其发展成同居关系。', '8.5', '1986-08-30');
