INSERT INTO `movie` VALUES ('182432', '踢到宝', '狠心舅父陈有（黄秋生 饰）串同贼人绑架外甥小宝（小柏林 饰），小宝不幸遇害。其父母伤心过度，决定移民离开这个伤心地。临行前他们为屋子装上先进的防盗锁，而巨额赎金和小宝的鬼魂则留在屋中。', '8', '1992-06-10');
