INSERT INTO `movie` VALUES ('211425', '黑玫瑰义结金兰', '冰室伙记阿武暗恋的嗒糖被无恶不作的酸梅汤掳走，幸遇退隐江湖三十年的女侠黑玫瑰。黑玫瑰多年来将吕奇禁锢，希望动之以情，令吕奇回心转意。武与吕奇劝黑玫瑰重出江湖，对付酸梅汤。', '10', '1997-03-01');
