INSERT INTO `movie` VALUES ('222832', '李碧华鬼魅系列：奇幻夜', '影片改编自香港作家李碧华《夜》系列原着小说，台前幕后阵容几乎全是香港本土势力，主演包括梁家辉、任达华、顾美、陈慧琳、邵美琪、童爱玲、邵音音等，导演阵容则包括陈果、陈嘉上、李志毅、刘国昌，而任达华及泰迪罗宾除了有份出演，也兼任了导演一职，这也将是任达华的处女导。', '5.7', '2013-09-20');
