INSERT INTO `movie` VALUES ('263132', '3D 豪情', '陈庆嘉03年执导、古天乐和陈奕迅主演的三级片《豪情》叫好叫座，相隔10年，终于开拍续集《豪情3D》，只是主角换上杜汶泽、何超仪及一班日本AV女优。阿泽和何超仪联同拍摄队伍赴东京取景，昨日本报记者直击拍摄现场，见波涛汹涌，扮演AV男优的杜汶泽更大战现年26岁、因拥有42L上围而被封为「人类最强Body」的AV女优冲田杏梨，宇宙大战一触即发！', '5', '2014-03-25');
