INSERT INTO `movie` VALUES ('299041', '密宗圣手', '在川康一带财雄势大，统治呈百八十多个水陆码头的武林高手兼宏大的帮会组织着领首领曾六爷膝下仅有一女名青青，貌美如花，武功高强，由于年华已老，其父有意替她特色一位如意郎君，适值西藏高原举行一年一度的比武大会…', '0', '1976-02-20');
