INSERT INTO `movie` VALUES ('320638', '烂赌英雄', '公司小职员罗兰度（陈百祥 饰）好赌成性，又喜好吹牛，对自己的新任上司阿虹（高丽虹 饰）一见钟情，但却没有接近的机会。罗兰度与公司经理拜访赌王新哥，一时头脑发热令赌王在赌局中吃亏。不久，罗兰度的大龄处男好友阿帆（冯淬帆 饰）被自己强势的女友抛弃后急于成家，二人奔赴菲律宾，阿帆竟意外的找回一名温顺菲律宾少女为妻，在朋友中出尽风头。罗兰度亦开始对阿虹发动攻势，但后者申明最厌弃赌徒，于是罗兰度发誓戒赌，经历了重重考验，罗兰度终于向阿虹证明了自己的诚意，然而阿虹的父亲居然是赌王新哥，为了让新哥接纳自己，罗兰度只好再赴赌场……', '0', '1987-03-31');
