INSERT INTO `movie` VALUES ('328862', '老港正传', '老左（黄秋生 饰）是香港老一代彻头彻尾的左派，他哼唱《歌唱祖国》、《智取老虎山》，高举为人民服务的伟大旗帜。他一生最大的愿望就是能到北京天安门去看看。', '4', '2007-06-15');
