INSERT INTO `movie` VALUES ('344057', '巴黎假期', '在巴黎，女画家丁晓敏（郭采洁 饰）为帮男友徐晖（徐正曦 饰）实现艺术梦想甘愿停学打工牺牲自己，然而却惨遭抛弃，自此变得癫癫丧丧、自暴自弃。正巧，居家好男人林俊杰（古天乐 饰）受邀来到巴黎打理酒庄，阴差阳错下假扮成gay和丁晓敏同居一屋。为了帮助丁晓敏走出感情创伤，林俊杰体贴地照顾起她的生活……', '6', '2015-07-23');
