INSERT INTO `movie` VALUES ('376168', '芳草碧连天', '反映80年代的台湾农村生活,城市来的专科学校毕业的女技术员凌云峰(王祖贤饰)指导农民改种花卉,老一辈村民反对,而农校毕业的青年周永健(齐秦饰)在推广花卉种植技术的过程中给予大力支持,成为其助手,继而两人相爱.花卉种植成功,农民收入增加,村舍面貌改观.年轻一代在共同战胜困难中产生了爱情.', '0', '1987-01-01');
