INSERT INTO `movie` VALUES ('403427', '冒险游戏', '云端、GUCCI、小威、狄克本是不同世界的人，然而这四个命运轨迹不同的人却在某一天闯进了彼此的生活。狄克从美国回来寻找初恋情人，但找到后却发现初恋情人现已判若两人，情已不再。而小威则发觉认识GUCCI后，不再寂寞，云端跳出亚龙的爱情迷阵，却又从亚龙身上惹来了江湖大祸，他们正陷入一场贩毒与追杀的困境。', '0', '1995-03-09');
