INSERT INTO `movie` VALUES ('407530', '标错参', '在一家珠宝贸易公司工作的肥丁（洪金宝）因是胖仔外加做人老实，常被老板娘周太（梁珊）和其弟（曹查理）骑在头上，重活脏活全要揽下不说，工作稍微出点错就会被骂得狗血淋头。一日，他和在货运部工作的同事阿B（钟镇涛）一起按老板吩咐送钻石给中东酋长，之后将所收钱款送往银行的途中遭劫，周太和其弟不问青红诬告两人与劫匪是同伙。', '9', '1987-12-03');
