INSERT INTO `movie` VALUES ('410578', '同居关系', '阿Paul从澳门来港，与友人Alex开设发廊。发廊内Alex结识了Eliza，Alex的热诚，绅绅感动Eliza，使得Eliza恋上这个不羁的发型师。另一方面，Paul则因为找寻新居而结识地产经纪阿Ann，二人志趣相投，遂激发阿Ann离开她在新界的旧居，与阿Paul共赋同居，从新的同居生活中，二人真的堕入爱河。两对青年男女经过一波三折，终于走上快乐的爱情道上。', '0', '1993-12-09');
