INSERT INTO `movie` VALUES ('487980', '假如王子睡着了', '王小禾（林允 饰）自幼便失去了父母，孤苦无依的长大，虽然身世可怜，但小禾的内心里却依旧保持着快乐和纯真。如今，小禾孤身一人在偌大的城市之中，靠着打工自己养活自己，她的梦想就是能够赚够学费，进入大学深造。', '0', '2017-12-08');
