INSERT INTO `movie` VALUES ('490297', '天神剑女之石头记', '女娲补天”遗弃的补天石来到人间，自称石中之玉神，与凡间铸建女红花相爱，但遭到天神无啸九天的阻挠并派正神下凡拆散他们，正神被这对鸳鸯真诚的爱情所感动，随不畏天规以自己的生命帮助他们练就神剑，经过艰苦奋战终于击败天神无啸九天，换来了终生的幸福。', '0', '1992-01-01');
