INSERT INTO `movie` VALUES ('491305', '海上浮城', '猪农老王被合作社骗了钱，祸不单行，他的猪又莫名地死去，这打破了美容院老板娘王招娣、在大城市打拼的儿子王振、富家女夏夏、抱有“中国梦”的美国建筑师肖恩的生活，在永不放弃的挣扎中他们的生活最终找回了各自的平衡点。  　　《海上浮城》是一部富含黑色喜剧元素的剧情片，以荒诞却真实的手法展现当代中国在现代化进程中老百姓的挣扎与拼搏，以超现实风格的结局给人提供一种逃离现实的感觉。', '8', '2018-01-19');
