INSERT INTO `movie` VALUES ('492851', '密战', '淞沪会战后上海沦陷，地下工作者林翔（郭富城 饰）受命来到危机四伏的上海，重建惨遭敌人破坏的地下抗日战线。在这里他遇到单纯却很有正义感的兰芳（赵丽颖 饰），这对临时组成的“地下党夫妇”将在战火纷飞中，携手与日本侵略者及伪政府特务展开惊险刺激的生死较量……', '4', '2017-11-03');
