INSERT INTO `movie` VALUES ('536524', '剃刀情人', '背景是一个黑帮新旧势力转换期，旧帮会老大因逃避警方而远走台湾，新帮会就乘机坐大，更欲取得帮会中的名册作巩固势力，肥豪一人到坤叔家里欲威迫坤交出名册。怎料坤早把名册资料输入电脑磁碟中，暗地交予老大的情妇清。坤夫妇更临危托孤，将独生女玲托清照顾。', '0', '1993-03-27');
