INSERT INTO `movie` VALUES ('552475', '狄仁杰之幽冥道', '大唐，边境小城发生连环凶杀案，民众夜间看到神秘兵团运送财宝。武则天（杨恭如 饰）令狄仁杰（罗立群 饰）前去探案，狄仁杰了解到见过神秘兵团的人接连丧命，就连现场尸首也呈现“沙化”现象。前来寻亲的李少侠与调查案件陷入困境中的狄仁杰携手解密，在两人的共同努力下，一场颠复大唐的阴谋逐渐浮出了水面......', '0', '2018-10-02');
