INSERT INTO `person` VALUES ('25245', '1961-07-10', null, '张学友', 'Jacky Cheung', '张学友，香港演员、歌星，是一位亚洲和华人极具影响力实力派音乐巨星、著名电影演员，香港乐坛“四大天王”之一，在华语地区享有“歌神”称誉。', 'Hong Kong');
