INSERT INTO `person` VALUES ('26755', '1950-03-24', null, '曹查理', 'Charlie Cho', '曹查理是邵氏公司出身的喜剧演员，以扮演惹人憎恶的衣冠禽兽和常被人捉弄的势利小人最令观众印象深刻，代表作品有《警察故事》、《最佳损友》、《猛鬼霸王花》等。而曹查理亦是张智霖的舅舅。当年张智霖之所以能顺利进入娱乐界发展，亦多赖这位舅舅的影响力。', 'Hong Kong');
