INSERT INTO `person` VALUES ('118350', '1964-07-02', null, '达Ming', 'Cheung Tat-Ming', '张达明（1964年7月2日生），生于香港，籍贯广东南海，香港喜剧演员、编剧及导演。1986年进入香港演艺学院修读戏剧系。后编写舞台剧及电影剧本，第一个电影剧本是《笑星撞地球》，继而组成“沙砖上”剧团参与多项幕前幕后学出。直至1995年与黄子华合作《栋笃笑双打》引起注意，开始正式加入电影演员行列，第一部演出影片为《山水有相逢》。目前是活跃于香港影坛的黄金配角，同时在主持上也非常用功，甚有人缘。', 'Hong Kong, China');
