INSERT INTO `person` VALUES ('120725', '1958-08-10', null, '于荣光', 'Yu Rong-Guang', '于荣光，中国内地知名演员。11岁考入北京市风雷京剧团任演员学习武生,自幼学习京剧十载。七十年代末崭露头角，成为剧团中坚力量，并在全国多次大奖赛中获奖。其京剧代表作有《挑滑车》、《伐子都》、《三岔口》、《战马超》等。', 'Beijing, China');
