INSERT INTO `person` VALUES ('148884', '1965-08-06', null, '钱嘉乐', 'Chin Ka-Lok', '钱嘉乐，香港电影及电视演员、节目主持、武术指导及导演。钱嘉乐是武打演员钱小豪胞弟。钱嘉乐初入行时为港产片的龙虎武师，幕前演出不多，故其名气不及其兄钱小豪，直至1996年被曾志伟邀请代替陈小春于无线电视《超级无敌奖门人》系列担任“奖老”，也主持过一些格调轻松的综艺节目。', 'Hong Kong, China');
